mapper.select:
SELECT

FROM t_sys_log a

WHERE a.id = #{id}

SELECT
FROM t_sys_log a
a.status = #{STATUS_NORMAL}
AND a.title LIKE '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND a.type = #{type}
AND ( a.title LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') OR a.ip LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') OR a.remark LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') OR u.name LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') OR u.login_name LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') )
AND a.type = #{type}
ORDER BY ${entityPage.orderBy} ${entityPage.order}
ORDER BY a.update_time DESC

SELECT

FROM t_sys_log a


	a.status = #{STATUS_NORMAL}



		ORDER BY ${entityPage.orderBy} ${entityPage.order}


		ORDER BY a.update_time DESC

SELECT
count(*)
FROM
t_sys_log t
WHERE
t.action LIKE  CONCAT('%', '用户登录', '%')
AND t.user_id = #{userId}


		AND a.create_time BETWEEN #{startTime} AND #{endTime}


		AND a.create_time >= #{startTime}


		AND a.create_time <= #{endTime}

SELECT
FROM t_sys_log a
WHERE a.title IS NOT NULL AND a.module = #{module} LIMIT 1;

mapper.update:
UPDATE t_sys_log SET
status = #{STATUS_DELETE}
WHERE id = #{id}
