mapper.select:
SELECT

FROM t_sys_organ a

WHERE a.id = #{id}

SELECT

FROM t_sys_organ a

WHERE a.status = '0' AND a.code = #{code}

SELECT

FROM t_sys_organ a

WHERE a.status = '0' AND a.sys_code = #{code}

SELECT

FROM t_sys_organ a


    a.status = #{status}



        ORDER BY ${entityPage.orderBy} ${entityPage.order}


        ORDER BY a.update_time DESC

SELECT

FROM t_sys_organ a



        a.status = #{status}


        a.code = #{code}


        a.type = #{type}



ORDER BY a.sort asc

SELECT a.id FROM t_sys_user a LEFT JOIN t_sys_user_organ ur ON ur.user_id = a.id LEFT JOin t_sys_organ o ON o.id = ur.organ_id
a.status = '0' AND o.id = #{organId}
ORDER BY a.sort asc

SELECT a.id FROM t_sys_organ a LEFT JOIN t_sys_user_organ uo ON uo.organ_id = a.id LEFT JOIN t_sys_user u ON u.id = uo.user_id
a.status = '0' AND u.id = #{userId}
ORDER BY a.sort asc

SELECT a.id FROM t_sys_organ a LEFT JOIN t_sys_post_organ po ON po.organ_id = a.id LEFT JOIN t_sys_post post ON post.id = po.post_id
a.status = '0' AND post.id = #{postId}
ORDER BY a.sort asc

mapper.update:
UPDATE t_sys_organ SET
parent_id = #{parent.id},
parent_ids = #{parentIds}
WHERE id = #{id}

mapper.delete:
UPDATE t_sys_organ SET
status = #{STATUS_DELETE},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime}
WHERE id = #{id}
